import{_ as s}from"./BreadCrumbs.4053aa8a.js";import{_ as i}from"./ServicesLinks.2d109f31.js";import{_}from"./GoUp.40b1ee49.js";import{a as l,b as n,c as p}from"./entry.8f66b6e8.js";import{F as r,H as t,y as a,G as f,m,U as v,V as u}from"./swiper-vue.cf596ed4.js";import"./nuxt-link.eea73dbd.js";import"./index.82c35a84.js";import"./servicesList.0e20c2a0.js";const h=e=>(v("data-v-ed46f6c9"),e=e(),u(),e),x=h(()=>t("div",{class:"bg white-theme-only gradient-bg"},null,-1)),S=f('<div class="top" data-v-ed46f6c9><h1 data-v-ed46f6c9>Таможенное оформление</h1><p data-v-ed46f6c9> Разъясняем нюансы законодательства и ведения внешнеэкономической деятельности. При необходимости заменяем для бизнеса отдел логистики и закупок — берем эти функциональные обязанности на аутсорсинг. Решаем любые вопросы и проблемы, связанные с организацией и выполнением международных грузовых перевозок. </p></div><p class="text" data-v-ed46f6c9> В лице нашей компании Вы получаете команду опытных специалистов. В арсенале сотрудников — годы работы с таможенными и транспортными, с сертификационными и иными разрешительными органами. Выступаем в качестве лицензионного представителя (брокера) во многих транспортных узлах. В частности, это порты Санкт-Петербург, Владивосток и Восточный, сухопутные пограничные переходы между РФ и КНР. </p><h2 data-v-ed46f6c9>Какие услуги мы оказываем?</h2><ul data-v-ed46f6c9><li data-v-ed46f6c9> Консультации по применению профильного законодательства и расчету пошлин. </li><li data-v-ed46f6c9> Помощь в проведении временного вывоза или ввоза грузов. </li><li data-v-ed46f6c9>Составление внешнеторговых контрактов.</li><li data-v-ed46f6c9>Декларирование товаров.</li><li data-v-ed46f6c9>Регистрация паспортов сделок.</li><li data-v-ed46f6c9>Подготовка и подача необходимого пакета документов.</li><li data-v-ed46f6c9> Сопровождение или представительство для скорейшего и правильного проведения процедуры таможенного оформления. </li><li data-v-ed46f6c9> Выполнение функций контрактодержателя. Берем на себя все мероприятия, которые требуются при заключении внешнеэкономической сделки. Подписание договора с иностранным контрагентом и импорт товаров осуществляется от нашего имени, но по поручению клиента. Мы самостоятельно покупаем продукцию за рубежом, а потом передаем ее Вам уже на российской территории. При этом собираем все требуемые документы, а также оплачиваем за свой счет таможенное оформление, НДС и импортную (ввозную) пошлину. Вы избавляетесь от необходимости даже минимально взаимодействовать с государственными органами. Вам не придется регистрироваться в качестве участника ВЭД, заключать валютную сделку и переводить деньги за товар иностранному продавцу. </li></ul><p data-v-ed46f6c9> Вы всегда можете уточнить условия и порядок таможенного оформления в компании «ТИС ЛОДЖИСТИК». Для этого свяжитесь с нашими менеджерами — используйте любой удобный способ: </p><ul data-v-ed46f6c9><li data-v-ed46f6c9>по телефону 8 800 234-80-70</li><li data-v-ed46f6c9>или электронной почте info@tislogistic.ru</li></ul>',6),b={class:"bottom"},g={__name:"tamozhennoe-oformlenie",setup(e){return n(),p({title:"Таможенное оформление"}),(y,B)=>{const o=s,d=i,c=_;return m(),r("div",null,[t("section",null,[x,t("div",null,[a(o,{class:"breadcrumbs"}),S,t("div",b,[a(d)]),a(c)])])])}}},O=l(g,[["__scopeId","data-v-ed46f6c9"]]);export{O as default};
