import{_ as i}from"./BreadCrumbs.14866252.js";import{_ as b}from"./ServicesLinks.35ceaa43.js";import{_}from"./GoUp.7af280e5.js";import{a as l,b as n,c}from"./entry.79df3faa.js";import{F as p,H as t,y as a,G as r,m,U as v,V as u}from"./swiper-vue.27e78005.js";import"./nuxt-link.27a9c7c3.js";import"./index.781e7d23.js";import"./servicesList.e9ab9919.js";const h=e=>(v("data-v-edb6335b"),e=e(),u(),e),f=h(()=>t("div",{class:"bg white-theme-only gradient-bg"},null,-1)),x=r('<div class="top" data-v-edb6335b><h1 data-v-edb6335b>Таможенное оформление</h1><p data-v-edb6335b> Разъясняем нюансы законодательства и ведения внешнеэкономической деятельности. При необходимости заменяем для бизнеса отдел логистики и закупок — берем эти функциональные обязанности на аутсорсинг. Решаем любые вопросы и проблемы, связанные с организацией и выполнением международных грузовых перевозок. </p></div><p class="text" data-v-edb6335b> В лице нашей компании Вы получаете команду опытных специалистов. В арсенале сотрудников — годы работы с таможенными и транспортными, с сертификационными и иными разрешительными органами. Выступаем в качестве лицензионного представителя (брокера) во многих транспортных узлах. В частности, это порты Санкт-Петербург, Владивосток и Восточный, сухопутные пограничные переходы между РФ и КНР. </p><h2 data-v-edb6335b>Какие услуги мы оказываем?</h2><ul data-v-edb6335b><li data-v-edb6335b> Консультации по применению профильного законодательства и расчету пошлин. </li><li data-v-edb6335b> Помощь в проведении временного вывоза или ввоза грузов. </li><li data-v-edb6335b>Составление внешнеторговых контрактов.</li><li data-v-edb6335b>Декларирование товаров.</li><li data-v-edb6335b>Регистрация паспортов сделок.</li><li data-v-edb6335b>Подготовка и подача необходимого пакета документов.</li><li data-v-edb6335b> Сопровождение или представительство для скорейшего и правильного проведения процедуры таможенного оформления. </li><li data-v-edb6335b> Выполнение функций контрактодержателя. Берем на себя все мероприятия, которые требуются при заключении внешнеэкономической сделки. Подписание договора с иностранным контрагентом и импорт товаров осуществляется от нашего имени, но по поручению клиента. Мы самостоятельно покупаем продукцию за рубежом, а потом передаем ее Вам уже на российской территории. При этом собираем все требуемые документы, а также оплачиваем за свой счет таможенное оформление, НДС и импортную (ввозную) пошлину. Вы избавляетесь от необходимости даже минимально взаимодействовать с государственными органами. Вам не придется регистрироваться в качестве участника ВЭД, заключать валютную сделку и переводить деньги за товар иностранному продавцу. </li></ul><p data-v-edb6335b> Вы всегда можете уточнить условия и порядок таможенного оформления в компании «Тис Лоджистик». Для этого свяжитесь с нашими менеджерами — используйте любой удобный способ: </p><ul data-v-edb6335b><li data-v-edb6335b>по телефону 8 800 234-80-70</li><li data-v-edb6335b>или электронной почте info@tislogistic.ru</li></ul>',6),S={class:"bottom"},g={__name:"tamozhennoe-oformlenie",setup(e){return n(),c({title:"Таможенное оформление"}),(y,B)=>{const o=i,d=b,s=_;return m(),p("div",null,[t("section",null,[f,t("div",null,[a(o,{class:"breadcrumbs"}),x,t("div",S,[a(d)]),a(s)])])])}}},O=l(g,[["__scopeId","data-v-edb6335b"]]);export{O as default};
